require('vstudio')

local function premakeVersionComment(prj)
    premake.w('<!-- Generated by Premake ' .. _PREMAKE_VERSION .. ' -->')
end

premake.override(premake.vstudio.vc2010.elements, "project", function(base, prj)
    local calls = base(prj)
    table.insertafter(calls, premake.vstudio.vc2010.xmlDeclaration, premakeVersionComment)
    return calls
end)

include "../Extension/Apis/vspropertysettings.lua"
include "../Extension/Apis/vsadditionalprops.lua"
include "../Extension/Apis/vsadditionaltargets.lua"
